import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import api from "../api/axios";
import { TaskItem, Project } from "../models/models";

export default function ProjectDetailsPage() {
  const { id } = useParams<{ id: string }>();
  const [project, setProject] = useState<Project | null>(null);
  const [tasks, setTasks] = useState<TaskItem[]>([]);
  const [newTaskTitle, setNewTaskTitle] = useState("");

  const fetchProject = async () => {
    const res = await api.get<Project>(`/projects/${id}`);
    setProject(res.data);
    setTasks(res.data.tasks || []);
  };

  const addTask = async () => {
    if (!newTaskTitle.trim()) return;
    await api.post("/tasks", { title: newTaskTitle, projectId: Number(id) });
    setNewTaskTitle("");
    fetchProject();
  };

  const toggleCompletion = async (taskId: number, completed: boolean) => {
    await api.put(`/tasks/${taskId}`, { completed });
    fetchProject();
  };

  const deleteTask = async (taskId: number) => {
    await api.delete(`/tasks/${taskId}`);
    fetchProject();
  };

  useEffect(() => {
    fetchProject();
  }, [id]);

  return (
    <div className="p-6 max-w-3xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">{project?.title}</h1>

      <div className="flex mb-4 gap-2">
        <input
          type="text"
          placeholder="New task title"
          value={newTaskTitle}
          onChange={(e) => setNewTaskTitle(e.target.value)}
          className="flex-1 border p-2 rounded-l"
        />
        <button
          onClick={addTask}
          className="bg-blue-500 text-white px-4 rounded-r hover:bg-blue-600"
        >
          Add Task
        </button>
      </div>

      <ul className="space-y-3">
        {tasks.map((task) => {
          const dueDate = new Date(task.dueDate || "");
          const isOverdue = dueDate && dueDate < new Date() && !task.completed;
          return (
            <li
              key={task.id}
              className="flex justify-between items-center p-4 border rounded-lg shadow-sm bg-white"
            >
              <div className="flex items-center gap-3">
                <input
                  type="checkbox"
                  checked={task.completed}
                  onChange={(e) =>
                    toggleCompletion(task.id, e.target.checked)
                  }
                  className="w-5 h-5"
                />
                <span
                  className={`${
                    task.completed ? "line-through text-gray-400" : ""
                  }`}
                >
                  {task.title}
                </span>
              </div>

              <div className="flex items-center gap-4">
                {dueDate && (
                  <span
                    className={`text-sm ${
                      isOverdue ? "text-red-500" : "text-gray-600"
                    }`}
                  >
                    Due: {dueDate.toLocaleDateString()}
                  </span>
                )}

                <button
                  onClick={() => deleteTask(task.id)}
                  className="text-red-500 hover:text-red-700"
                >
                  Delete
                </button>
              </div>
            </li>
          );
        })}
      </ul>
    </div>
  );
}
