import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import api from "../api/axios";
import { TaskItem } from "../models/models";

export default function ProjectDetailsPage() {
  const { id } = useParams();
  const [tasks, setTasks] = useState<TaskItem[]>([]);
  const [title, setTitle] = useState("");
  const [projectTitle, setProjectTitle] = useState("");

  const fetchTasks = async () => {
    const res = await api.get<TaskItem[]>(`/tasks/project/${id}`);
    setTasks(res.data);
  };

const fetchProjectTitle = async () => {
    const res = await api.get(`/projects/${id}`);
    setProjectTitle(res.data.title);
  };

  const addTask = async () => {
    if (!title) return;
    await api.post("/tasks", { title, projectId: Number(id) });
    setTitle("");
    fetchTasks();
  };

  const toggleTask = async (task: TaskItem) => {
    await api.put(`/tasks/${task.id}`, { isCompleted: !task.isCompleted });
    fetchTasks();
  };

  const deleteTask = async (taskId: number) => {
    await api.delete(`/tasks/${taskId}`);
    fetchTasks();
  };

  useEffect(() => {
    fetchTasks();
  }, [id]);

  return (
    <div className="p-6 max-w-3xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">Tasks</h1>

      <div className="flex mb-4">
        <input
          type="text"
          placeholder="New task title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          className="border p-2 flex-1 rounded-l"
        />
        <button
          onClick={addTask}
          className="bg-green-500 text-white px-4 rounded-r hover:bg-green-600"
        >
          Add
        </button>
      </div>

      <ul>
        {tasks.map((t) => (
          <li
            key={t.id}
            className="flex justify-between items-center border-b py-2"
          >
            <span
              onClick={() => toggleTask(t)}
              className={`cursor-pointer ${
                t.isCompleted ? "line-through text-gray-400" : ""
              }`}
            >
              {t.title}
            </span>
            <button
              onClick={() => deleteTask(t.id)}
              className="text-red-500 hover:text-red-700"
            >
              Delete
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}
