import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import api from "../api/axios";
import { TaskItem } from "../models/models";

export default function ProjectDetailsPage() {
  const { id } = useParams();
  const [tasks, setTasks] = useState<TaskItem[]>([]);
  const [title, setTitle] = useState("");
  const [dueDate, setDueDate] = useState("");
  const [projectTitle, setProjectTitle] = useState("");

  const fetchTasks = async () => {
    const res = await api.get<TaskItem[]>(`/tasks/project/${id}`);
    setTasks(res.data);
  };

  const fetchProjectTitle = async () => {
    const res = await api.get(`/projects/${id}`);
    setProjectTitle(res.data.title);
  };

  const addTask = async () => {
    if (!title) return;
    await api.post("/tasks", { title, projectId: Number(id), dueDate });
    setTitle("");
    setDueDate("");
    fetchTasks();
  };

  const toggleTask = async (task: TaskItem) => {
    await api.put(`/tasks/${task.id}`, { isCompleted: !task.isCompleted });
    fetchTasks();
  };

  const deleteTask = async (taskId: number) => {
    await api.delete(`/tasks/${taskId}`);
    fetchTasks();
  };

  useEffect(() => {
    fetchTasks();
    fetchProjectTitle();
  }, [id]);

  return (
    <div className="p-6 max-w-2xl mx-auto">
      {/* Project Title */}
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-2xl font-bold text-gray-800">{projectTitle}</h1>
        <button
          onClick={() => window.history.back()}
          className="text-sm text-gray-500 hover:underline"
        >
          ‚Üê Back to Projects
        </button>
      </div>

      {/* Add Task Section */}
      <div className="bg-gray-50 p-4 rounded-lg shadow mb-6">
        <h2 className="text-lg font-semibold mb-3 text-gray-700">Add New Task</h2>
        <div className="flex flex-col md:flex-row gap-3">
          <input
            type="text"
            placeholder="Task title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            className="border p-2 flex-1 rounded"
          />
          <input
            type="date"
            value={dueDate}
            onChange={(e) => setDueDate(e.target.value)}
            className="border p-2 rounded"
          />
          <button
            onClick={addTask}
            className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
          >
            Add
          </button>
        </div>
      </div>

      {/* Task List */}
      <div>
        <h2 className="text-lg font-semibold mb-3 text-gray-700">Tasks</h2>
        {tasks.length === 0 ? (
          <p className="text-gray-500">No tasks yet. Add one above!</p>
        ) : (
          <ul className="space-y-3">
            {tasks.map((t) => {
              const due = t.dueDate ? new Date(t.dueDate) : null;
              const isOverdue = due && !t.isCompleted && due < new Date();
              return (
                <li
                  key={t.id}
                  className={`flex justify-between items-center p-3 border rounded-lg shadow-sm bg-white ${
                    t.isCompleted ? "opacity-60" : ""
                  }`}
                >
                  <div className="flex items-center gap-3">
                    <input
                      type="checkbox"
                      checked={t.isCompleted}
                      onChange={() => toggleTask(t)}
                      className="w-5 h-5"
                    />
                   <div className="flex flex-col">
  <p className={`font-medium text-gray-800 ${t.isCompleted ? "line-through" : ""}`}>
    {t.title}
  </p>

  {due && (
    <span
      className={`
        mt-1 inline-block px-2 py-0.5 text-xs font-semibold rounded
        ${
          t.isCompleted
            ? "bg-gray-300 text-gray-700"
            : isOverdue
            ? "bg-red-500 text-white"
            : due.getTime() - new Date().getTime() <= 2 * 24 * 60 * 60 * 1000
            ? "bg-yellow-400 text-"
            : "bg-green-200 text-green-800"
        }
      `}
    >
      {isOverdue
        ? "Overdue: " + due.toLocaleDateString()
        : "Due: " + due.toLocaleDateString()}
    </span>
  )}
</div>


                  </div>

                  <button
                    onClick={() => deleteTask(t.id)}
                    className="text-red-500 hover:text-red-700"
                  >
                    Delete
                  </button>
                </li>
              );
            })}
          </ul>
        )}
      </div>
    </div>
  );
}
