import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import api from "../api/axios";
import { Project } from "../models/models";

export default function DashboardPage() {
  const [projects, setProjects] = useState<Project[]>([]);
  const [title, setTitle] = useState("");
  const [editingDescId, setEditingDescId] = useState<number | null>(null);
  const [descValue, setDescValue] = useState("");
  const navigate = useNavigate();

  const fetchProjects = async () => {
    const res = await api.get<Project[]>("/projects");
    setProjects(res.data);
  };

  const addProject = async () => {
    if (!title) return;
    await api.post("/projects", { title });
    setTitle("");
    fetchProjects();
  };

  const deleteProject = async (id: number) => {
    await api.delete(`/projects/${id}`);
    fetchProjects();
  };

  const saveDescription = async (projectId: number) => {
    await api.put(`/projects/${projectId}`, { description: descValue });
    setEditingDescId(null);
    setDescValue("");
    fetchProjects();
  };

  useEffect(() => {
    fetchProjects();
  }, []);

  return (
    <div className="p-6 max-w-3xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">My Projects</h1>

      <div className="flex mb-4">
        <input
          type="text"
          placeholder="New project title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          className="border p-2 flex-1 rounded-l"
        />
        <button
          onClick={addProject}
          className="bg-blue-500 text-white px-4 rounded-r hover:bg-blue-600"
        >
          Add
        </button>
      </div>

      <ul>
        {projects.map((p) => (
          <li
            key={p.id}
            className="flex flex-col border-b py-2 mb-2"
          >
            <div className="flex justify-between items-center">
              <span
                className="cursor-pointer text-blue-600 hover:underline"
                onClick={() => navigate(`/projects/${p.id}`)}
              >
                {p.title}
              </span>
              <button
                onClick={() => deleteProject(p.id)}
                className="text-red-500 hover:text-red-700"
              >
                Delete
              </button>
            </div>

            {editingDescId === p.id ? (
              <div className="mt-2 flex space-x-2">
                <input
                  type="text"
                  value={descValue}
                  onChange={(e) => setDescValue(e.target.value)}
                  className="border p-1 flex-1 rounded"
                />
                <button
                  onClick={() => saveDescription(p.id)}
                  className="bg-green-500 text-white px-2 rounded hover:bg-green-600"
                >
                  Save
                </button>
                <button
                  onClick={() => setEditingDescId(null)}
                  className="bg-gray-300 px-2 rounded hover:bg-gray-400"
                >
                  Cancel
                </button>
              </div>
            ) : (
              <div className="mt-1 flex justify-between items-center">
                <p>{p.description || "No description yet."}</p>
                <button
                  onClick={() => {
                    setEditingDescId(p.id);
                    setDescValue(p.description || "");
                  }}
                  className="text-blue-500 hover:underline text-sm"
                >
                  {p.description ? "Edit" : "Add"} Description
                </button>
              </div>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
}
