import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { DndProvider, useDrag, useDrop } from "react-dnd";
import { HTML5Backend } from "react-dnd-html5-backend";
import api from "../api/axios";
import { TaskItem } from "../models/models";

interface DraggableTaskProps {
  task: TaskItem;
  index: number;
  moveTask: (from: number, to: number) => void;
  toggleTask: (task: TaskItem) => void;
  deleteTask: (taskId: number) => void;
}

const DraggableTask: React.FC<DraggableTaskProps> = ({
  task,
  index,
  moveTask,
  toggleTask,
  deleteTask,
}) => {
  const ref = useRef<HTMLLIElement>(null);

  const [, drop] = useDrop({
    accept: "TASK",
    hover(item: { index: number }) {
      if (item.index !== index) {
        moveTask(item.index, index);
        item.index = index;
      }
    },
  });

  const [{ isDragging }, drag] = useDrag({
    type: "TASK",
    item: { index },
    collect: (monitor) => ({
      isDragging: monitor.isDragging(),
    }),
  });

  drag(drop(ref));

  const due = task.dueDate ? new Date(task.dueDate) : null;
  const isOverdue = due && !task.isCompleted && due < new Date();

  const priorityColors: Record<string, string> = {
    Low: "bg-green-200 text-green-800",
    Medium: "bg-yellow-200 text-yellow-800",
    High: "bg-red-200 text-red-800",
  };

  return (
    <li
      ref={ref}
      className={`flex justify-between items-center p-3 border rounded-lg shadow-sm bg-white ${
        isDragging ? "opacity-50" : ""
      } ${task.isCompleted ? "opacity-60" : ""}`}
    >
      <div className="flex items-center gap-3">
        <input
          type="checkbox"
          checked={task.isCompleted}
          onChange={() => toggleTask(task)}
          className="w-5 h-5"
        />
        <div>
          <p
            className={`font-medium text-gray-800 ${
              task.isCompleted ? "line-through" : ""
            }`}
          >
            {task.title}
          </p>
          <div className="flex gap-2 items-center">
            {due && (
              <p className={`text-sm ${isOverdue ? "text-red-500" : "text-gray-500"}`}>
                Due: {due.toLocaleDateString()}
              </p>
            )}
            {task.priority && (
              <span
                className={`text-xs font-semibold px-2 py-0.5 rounded ${priorityColors[task.priority]}`}
              >
                {task.priority}
              </span>
            )}
          </div>
        </div>
      </div>
      <button
        onClick={() => deleteTask(task.id)}
        className="text-red-500 hover:text-red-700"
      >
        Delete
      </button>
    </li>
  );
};

export default function ProjectDetailsPage() {
  const { id } = useParams();
  const [tasks, setTasks] = useState<TaskItem[]>([]);
  const [title, setTitle] = useState("");
  const [dueDate, setDueDate] = useState("");
  const [priority, setPriority] = useState("Medium");
  const [projectTitle, setProjectTitle] = useState("");

  const fetchTasks = async () => {
    const res = await api.get<TaskItem[]>(`/tasks/project/${id}`);
    setTasks(res.data);
  };

  const fetchProjectTitle = async () => {
    const res = await api.get(`/projects/${id}`);
    setProjectTitle(res.data.title);
  };

  const addTask = async () => {
    if (!title) return;
    await api.post("/tasks", { title, projectId: Number(id), dueDate, priority });
    setTitle("");
    setDueDate("");
    setPriority("Medium");
    fetchTasks();
  };

  const toggleTask = async (task: TaskItem) => {
    await api.put(`/tasks/${task.id}`, { isCompleted: !task.isCompleted });
    fetchTasks();
  };

  const deleteTask = async (taskId: number) => {
    await api.delete(`/tasks/${taskId}`);
    fetchTasks();
  };

  const moveTask = (fromIndex: number, toIndex: number) => {
    const updatedTasks = [...tasks];
    const [moved] = updatedTasks.splice(fromIndex, 1);
    updatedTasks.splice(toIndex, 0, moved);
    setTasks(updatedTasks);
    // Optionally: send the new order to backend
  };

  useEffect(() => {
    fetchTasks();
    fetchProjectTitle();
  }, [id]);

  return (
    <DndProvider backend={HTML5Backend}>
      <div className="p-6 max-w-2xl mx-auto">
        {/* Project Title */}
        <div className="flex justify-between items-center mb-6">
          <h1 className="text-2xl font-bold text-gray-800">{projectTitle}</h1>
          <button
            onClick={() => window.history.back()}
            className="text-sm text-gray-500 hover:underline"
          >
            ‚Üê Back to Projects
          </button>
        </div>

        {/* Add Task Section */}
        <div className="bg-gray-50 p-4 rounded-lg shadow mb-6">
          <h2 className="text-lg font-semibold mb-3 text-gray-700">Add New Task</h2>
          <div className="flex flex-col md:flex-row gap-3">
            <input
              type="text"
              placeholder="Task title"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              className="border p-2 flex-1 rounded"
            />
            <input
              type="date"
              value={dueDate}
              onChange={(e) => setDueDate(e.target.value)}
              className="border p-2 rounded"
            />
            <select
              value={priority}
              onChange={(e) => setPriority(e.target.value)}
              className="border p-2 rounded"
            >
              <option value="Low">Low</option>
              <option value="Medium">Medium</option>
              <option value="High">High</option>
            </select>
            <button
              onClick={addTask}
              className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
            >
              Add
            </button>
          </div>
        </div>

        {/* Task List */}
        <div>
          <h2 className="text-lg font-semibold mb-3 text-gray-700">Tasks</h2>
          {tasks.length === 0 ? (
            <p className="text-gray-500">No tasks yet. Add one above!</p>
          ) : (
            <ul className="space-y-3">
              {tasks.map((task, index) => (
                <DraggableTask
                  key={task.id}
                  task={task}
                  index={index}
                  moveTask={moveTask}
                  toggleTask={toggleTask}
                  deleteTask={deleteTask}
                />
              ))}
            </ul>
          )}
        </div>
      </div>
    </DndProvider>
  );
}
